import 'package:dev_dash/ui/packages/lib/widget_with_codeview.dart';import 'package:flutter/material.dart';import 'package:youtube_player_flutter/youtube_player_flutter.dart';class ToolTipWidget extends StatefulWidget {  const ToolTipWidget({super.key});  @override  State<ToolTipWidget> createState() => _ToolTipWidgetState();}class _ToolTipWidgetState extends State<ToolTipWidget> {  String url = 'https://www.youtube.com/watch?v=EeEfD5fI-5Q';  YoutubePlayerController? controller;  @override  void initState() {    final videoId = YoutubePlayer.convertUrlToId(url);    controller = YoutubePlayerController(      initialVideoId: videoId!,      flags: const YoutubePlayerFlags(        autoPlay: false,        mute: false,        enableCaption: true,        loop: true,        forceHD: true,      ),    );    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        leading: IconButton(          icon: const Icon(Icons.arrow_back, color: Colors.white),          onPressed: () => Navigator.of(context).pop(),        ),        title: const Text(          "Tooltip",          style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold),        ),        backgroundColor: Colors.black,      ),      body: Column(        children: [          Padding(            padding: const EdgeInsets.only(bottom: 8),            child: YoutubePlayer(              controller: controller!,              progressColors: const ProgressBarColors(                backgroundColor: Colors.black,                handleColor: Colors.white,              ),            ),          ),          const Expanded(            flex: 1,            child: WidgetWithCodeView(              filePath: 'lib/widgets/tooltip_widget.dart',              iconBackgroundColor: Colors.black,              iconForegroundColor: Colors.white,              codeLinkPrefix: 'https://google.com?q=',              codeContent: '''            import 'package:flutter/material.dart';                                        class TooltipExample extends StatefulWidget {              const TooltipExample({super.key});                          @override              State<TooltipExample> createState() => _TooltipExampleState();            }                        class _TooltipExampleState extends State<TooltipExample> {                          @override              Widget build(BuildContext context) {                return SingleChildScrollView(                  scrollDirection: Axis.vertical,                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [            const Padding(              padding: EdgeInsets.only(top: 18),            ),            Center(              child: Row(                mainAxisAlignment: MainAxisAlignment.center,                children: <Widget>[                  Container(                    color: Colors.blue[50],                    height: 175,                    width: 300,                    child: const Padding(                      padding: EdgeInsets.all(12.0),                      child: Tooltip(                        message: 'Text',                        child: Text(                          'Flutter is an open-source UI software development kit created by Google...',                          style: TextStyle(                            color: Colors.blue,                            fontSize: 25,                          ),                        ),                      ),                    ),                  ),                ],              ),            ),            // Example 2 Code                        const Center(              child: Row(                mainAxisAlignment: MainAxisAlignment.spaceEvenly,                children: [                  Tooltip(                    message: 'Settings Icon',                    child: Icon(Icons.settings),                  ),                  Tooltip(                    message: 'Close Icon',                    child: Icon(Icons.close),                  ),                  Tooltip(                    message: 'Profile Icon',                    child: Icon(Icons.person),                ),              ],            ),          ),        ],      ),    );  }}''',              child: TooltipExample(),            ),          ),        ],      ),    );  }}class TooltipExample extends StatefulWidget {  const TooltipExample({super.key});  @override  State<TooltipExample> createState() => _TooltipExampleState();}class _TooltipExampleState extends State<TooltipExample> {  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      scrollDirection: Axis.vertical,      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          const Padding(            padding: EdgeInsets.only(top: 18, left: 15),            child: Text(              'Properties of ToolTip Widget:',              style: TextStyle(fontWeight: FontWeight.bold, fontSize: 20),            ),          ),          const Padding(            padding: EdgeInsets.only(top: 18, left: 15),            child: Text(              '1.message\n'              '2.height,\n'              '3.padding,\n'              '4.margin,\n'              '5.verticalOffset,\n'              '6.preferBelow,\n'              '7.excludeFromSemantics,\n'              '8.decoration,\n'              '9.textStyle,\n'              '10.waitDuration,\n'              '11.showDuration,\n'              '12.child',              style: TextStyle(fontSize: 18),            ),          ),          const Divider(            thickness: 2,            height: 50,          ),          const Padding(            padding: EdgeInsets.only(top: 18, left: 15),            child: Text(              'Example 1: Hold on Text',              style: TextStyle(                fontWeight: FontWeight.bold,                fontSize: 17,                color: Colors.blue,              ),            ),          ),          const Padding(            padding: EdgeInsets.only(top: 18),          ),          Center(            child: Row(              mainAxisAlignment: MainAxisAlignment.center,              children: <Widget>[                Container(                  color: Colors.blue[50],                  height: 175,                  width: 300,                  child: const Padding(                    padding: EdgeInsets.all(12.0),                    child: Tooltip(                      message: 'Text',                      child: Text(                        'Flutter is an open-source UI software development kit created by Google...',                        style: TextStyle(                          color: Colors.black,                          fontSize: 25,                        ),                      ),                    ),                  ),                ),              ],            ),          ),          const Divider(            thickness: 2,            height: 50,          ),          const Padding(            padding: EdgeInsets.only(top: 18, left: 15),            child: Text(              'Example 2: Hold on Icons.',              style: TextStyle(                fontWeight: FontWeight.bold,                fontSize: 17,                color: Colors.blue,              ),            ),          ),          const Padding(            padding: EdgeInsets.only(top: 30),            child: Center(              child: Row(                mainAxisAlignment: MainAxisAlignment.spaceEvenly,                children: [                  Tooltip(                    message: 'Settings Icon',                    child: Icon(                      Icons.settings,                      size: 50,                    ),                  ),                  Tooltip(                    message: 'Close Icon',                    child: Icon(                      Icons.close,                      size: 50,                    ),                  ),                  Tooltip(                    message: 'Profile Icon',                    child: Icon(                      Icons.person,                      size: 50,                    ),                  ),                ],              ),            ),          ),          const Divider(            thickness: 2,            height: 50,          ),        ],      ),    );  }}